 <script>

       let __strapi_url='https://edicao.setma.co.mz:3003'


	   function update_actived_btns(){
	           try{

	               const btns=document.querySelectorAll('._main-slider ._navigation span')
	               btns.forEach((e,i)=>{
	               	 e.classList.remove('active')

	               	 if (i==index_s || (index_s + 1==slide_num && i==0)) {
	               	  	e.classList.add('active')
	               	 } 

	               })

               }catch(e){}
	}
	   

      function start_slide(res){

		    document.querySelector('._main-slider ._slides').innerHTML=""
            document.querySelector('._main-slider ._navigation').innerHTML=""
			document.querySelector('._main-slider ._slides').style.width=!res.data.length ? '100%' : ((res.data.length + 1) * 100)+`%`
           
            res.data.forEach((i,_i)=>{
				let img_url=i.attributes.imagem.data[0].attributes.url
				document.querySelector('._main-slider ._slides').innerHTML+=`<div class="_slide" style="background-image: url(${__strapi_url+img_url});background-size: cover;"></div>`
				document.querySelector('._main-slider ._navigation').innerHTML+=`<span onclick="change_slide_image('${_i}')"></span>`
             })



			 if(res.data.length){
				
                document.querySelector('._main-slider ._slides').innerHTML+=`<div class="_slide" style="background-image: url(${__strapi_url+res.data[0].attributes.imagem.data[0].attributes.url});background-size: cover;"></div>`

			 }

			 
			
			document.querySelector('._main-slider ._slide').style.transation="all 0.7s ease-in-out 0s"


			if(document.querySelectorAll('._main-slider ._slide').length <= 1) return

			const main_slider=document.querySelector('._main-slider ._slides')
	        const slide_num=document.querySelectorAll('._main-slider ._slide').length
	        let index_s=0 
			let main_slide_interval
			


			



	         function animate_slide() {

		           main_slide_interval=setInterval(()=>{

                    if(document.body.getAttribute('_ed_active')=="true"){
                        return
	         	    } 

	         	     const size=window.getComputedStyle(document.querySelectorAll('._main-slider ._slides > div')[index_s]).backgroundPositionY

	         	     document.querySelector('._ed_main_silde_img_position_range input').value=parseFloat(size)

                     

		           main_slider.style.transition="0.7s ease-in-out"
		           index_s++
		           main_slider.style.marginLeft=`-${index_s*100}%`

				   update_actived_btns()
		           
		           if(index_s>=slide_num - 1) {
		             	setTimeout(()=>{
		              	    index_s=0
		              	  	main_slider.style.transition="0s"
		              	  	main_slider.style.marginLeft=`0%`
		              	},730) 
		            }
		            

		           },6000)


	         }

			 animate_slide()
			 
		   
		   
		  




	  }
       const main_slider=document.querySelector('._main-slider ._slides')
	   let main_slide_timeout
            function change_slide_image(i){
            	 index_s=i
				 const size=window.getComputedStyle(document.querySelectorAll('._main-slider ._slides > div')[index_s]).backgroundPositionY

	             document.querySelector('._ed_main_silde_img_position_range input').value=parseFloat(size)
            	 main_slider.style.marginLeft=`-${index_s*100}%`
            	 main_slider.style.transition="0s"
            	 update_actived_btns()
            	 clearInterval(main_slide_interval)
            	 clearTimeout(main_slide_timeout)
            	 main_slide_timeout=setTimeout(()=>animate_slide(),6000)
            }


	  function get_slide_imgs(){
        fetch(__strapi_url+'/api/slide-principals?populate=*').then(response => {
          if (!response.ok) {
            throw new Error('Error');
          }
          return response.json();
        })
        .then(data => {
           start_slide(data)
        })
        .catch(error => {
          setTimeout(()=>get_slide_imgs(),3000)
          console.log(error)
       });
	  }
     get_slide_imgs()
 

const mainBoxMoveContainer=document.querySelector('._main-slider .box')

const mainBoxMove=mainBoxMoveContainer.querySelector('._main-slider ._dnnas-desc ._ed_header')


function onDrag({movementY,movementX}){
	let style=window.getComputedStyle(mainBoxMoveContainer)
	let top=parseInt(style.top)
	let left=parseInt(style.left)
    mainBoxMoveContainer.style.top=`${top + movementY}px`
    mainBoxMoveContainer.style.left=`${left + movementX}px`


}


mainBoxMove.addEventListener('mousedown',()=>{
	mainBoxMove.addEventListener('mousemove',onDrag)
})

mainBoxMove.addEventListener('mouseleave',()=>{
	mainBoxMove.removeEventListener('mousemove',onDrag)
})


